mapper.select:
SELECT custom_fnm, SUM (odr_qty) "odr_qty"
FROM v_order_order_analyze
WHERE fact_no = #{factNo}
  AND SUBSTR (rev_odr_date, 1, 4) = #{revOdrYear}
GROUP BY custom_fnm
ORDER BY SUM (odr_qty) DESC

SELECT custom_fnm, SUM (odr_qty) "odr_qty"
FROM v_order_order_analyze
WHERE fact_no = #{factNo}
  AND rev_odr_date BETWEEN #{dateStart} AND #{dateEnd}
GROUP BY custom_fnm
ORDER BY SUM (odr_qty) DESC
